      this.setState({A:-A});
      this.setState({B:300 -A * 387.5});

      //seTx(388);

      this.setState({y:299.6});
      if (x >= 500) {
        this.setState({F:1});
      } else if (x <= 0) {
        this.setState({F:0});
      }
    }
    if (F === 1) {
      this.setState({x:x-1});
    } else {
      seTx(x + 1);
      this.setState({x:x+1});

    }


    this.setState({y:A * x + B});

    //console.log(y);
  }

  render(){
  return (
    <div>
      <Stage width={window.innerWidth} height={window.innerHeight}>
        <Layer>
          <Rect x={0} y={0} width={500} height={300} stroke shadowBlur={10} />
          <Circle x={x} y={y} radius={10} fill="blue" />
        </Layer>
      </Stage>
    </div>
  );
};
}

ReactDOM.render(<App />, document.getElementById("root"));
