import ReactDOM from "react-dom";
import React  from "react";
import { Stage, Layer, Rect, Circle } from "react-konva";

class App extends React.Component {

constructor(props){
  super(props);
  this.state={

  A: 0.8,
  B: -10,
  x: 20,
  y: 6,
  F: 0


  };
}

  //const [A, seTA] = useState(0.8);
  //const [B, seTB] = useState(-10);
  //const [x, seTx] = useState(20);
  //const [y, seTy] = useState(6);
  //const [F, seTF] = useState(0);

  componentDidMount() {
    setTimeout(rotina, 10);

  function rotina() {
    if (this.state.x <= 0 || this.state.x >= 500 || this.state.y >= 300 || this.state.y <= 0) {



      this.setState({A:-this.state.A});
      this.setState({B:300 -this.state.A * 387.5});



      this.setState({y:299.6});
      if (this.state.x >= 500) {
        this.setState({F:1});
      } else if (this.state.x <= 0) {
        this.setState({F:0});
      }
    }
    if (this.state.F === 1) {
      this.setState({x:this.state.x-1});
    } else {

      this.setState({x:this.state.x+1});

    }


    this.setState({y:this.state.A * this.state.x + this.state.B});

  }
}

  render(){
  return (
    <div>
      <Stage width={window.innerWidth} height={window.innerHeight}>
        <Layer>
          <Rect x={0} y={0} width={500} height={300} stroke shadowBlur={10} />
          <Circle x={this.state.x} y={this.state.y} radius={10} fill="blue" />
        </Layer>
      </Stage>
    </div>
  );
};
}

ReactDOM.render(<App />, document.getElementById("root"));
